export class Counter extends React.Component {
  state = {
    count: this.props.initialValue
  };
  constructor(props) {
    super(props);
    setInterval(() => {
      this.setState((state) => {
        return { count: state.count + this.props.incrementValue };
      });
    }, this.props.incrementTime);
  }

