import React from "react";

export class Counter extends React.Component {
  state = {
    count: this.props.initialValue
  };
  constructor(props) {
    super(props);
    setInterval(() => {
      this.setState((state) => {
        return { count: state.count + this.props.incrementValue ?? 1 };
      });
    }, this.props.incrementTime );
  }

  render() {
    return (
      <div>
        <h1>{this.state.count}</h1>
      </div>
    );
  }
}
