import React from "react";

export class Login extends React.Component {
  state = {
    username: "",
    password: "",
    remember: false,
    buttonState: true,
  };

  // controlValueInput = (event) => {
  //   const name = event.target.name;
  //   const value = event.target.value;
  //   const type = event.target.type;
  //   const checked = event.target.checked;


  //   this.setState({


  //   });
  // };

  onLogin = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    const type = event.target.type;
    const disabled = event.target.disabled;
    const checked=event.target.checked

    this.setState({
      [name]: type === "button" ? disabled : value,
      [name]: type === "checkbox" ? checked : value,
      buttonState: !this.state.username && !this.state.password ? false : true,

      // username: this.state.username,
      // password: this.state.password,
      // remember: this.state.remember,

    });
  };

  componentDidUpdate() {
    console.log(this.state);
  }

  render() {
    return (
      <div>
        <input
          name="username"
          type="username"
          value={this.state.username}
          onChange={this.onLogin}
        ></input>
        <input
          type="password"
          name="password"
          value={this.state.password}
          onChange={this.onLogin}
        ></input>
        <input
          type="checkbox"
          name="remember"
          checked={this.state.remember}
          onChange={this.onLogin}
        ></input>
        <button
          name="button"
          type="button"
          disabled={this.state.buttonState}
          onChange={this.controlValueInput}
          onClick={this.onLogin}
        >
          Login
        </button>
      </div>
    );
  }
}
