export class ClickTraker extends React.Component {
    constructor(props){
        super(props)
            this.state= {value : ''};
        }


  trackerButtonClick = (event) => {
  console.log(event.target = this.state.value)

  };

  render() {
    return (
      <div>
        <button onClick={this.trackerButtonClick}value= 'button one'>Button one</button>
        <button onClick={this.trackerButtonClick}value= 'button two'>Button two</button>
        <button onClick={this.trackerButtonClick}value='button  three'>button three</button>
        <h1>{this.state.value} </h1>

      </div>
    );
  }
}
