  //  formIncompleted=(event)=>{
  //   this.setState({


  //   })
  //  }

  render() {
    return (
      <div>
        <input
          name="username"
          value={this.state.username}
          onChange={this.controlValueInput}
        ></input>
        <input
          type="password"
          name="password"
          value={this.state.password}
          onChange={this.controlValueInput}
        ></input>
        <input
          type="checkbox"
          name="remember"
          checked={this.state.remember}
          onChange={this.controlValueInput}
        ></input>
        <button disabled={this.state.buttonState} onClick={this.onLogin}>
            Login
        </button>
      </div>
    );
  }
}
