import React, { createRef } from "react";

export class UncontrolledLogin extends React.Component {
    _formRef= createRef()
  UncontrolledLogin = (event) => {
    event.preventDefault();

    const username = this._formRef.current.elements.username;
    const password = this._formRef.current.elements.password;
    const checkbox = this._formRef.current.checkbox.value;
    console.log({ username, password, checkbox });
  };



  render() {
    return (
      <div>
        <form ref={this._formRef}  onSubmit={this.UncontrolledLogin}>
          <input name="username"ref={this._formRef}onChange={this.UncontrolledLogin}></input>
          <input name="password" ref={this._formRef}type="password"onChange={this.UncontrolledLogin}></input>
          <input name="checkbox" ref={this._formRef}type="checkbox"onChange={this.UncontrolledLogin}></input>
          <button>Submit</button>


        </form>
      </div>
    );
  }
}
